<?php
namespace clear\Responder;
use clear\Router;
use clear\Route;
use clear\Render\Text;

require_once __DIR__ . "/../BaseCase.php";

/**
 * Test class for Route.
 * Generated by PHPUnit on 2011-12-13 at 08:29:38.
 */
class ResponderTest extends \BaseCase {
	
    function testInstantiateNoErrors()
    {
        new CliResponder(
            new Router(
                new Route('get', '/')
            ),
            new Text()
        );
        // just a sanity test
        $this->assertTrue(true);
    }
    
    function testComplete()
    {
        $responder = new CliResponder(
            new Router(
                new Route('get', '/')
            ),
            new Text()
        );
        $responder->complete();
        // just sanity check
        $this->assertTrue(true);
    }
    
}
